// customTheme.js

import { extendTheme } from "@chakra-ui/react";

const customTheme = extendTheme({
  colors: {
    primary: {
      50: "#f7faff",
      100: "#d0e0ff",
      200: "#a2b9ff",
      300: "#758fff",
      400: "#5370ff",
      500: "#344ed9", // Change the primary color here
      600: "#2a3ca9",
      700: "#1f2980",
      800: "#131d5e",
      900: "#0b0f3b",
    },
    // You can define additional color palettes if needed
    // For example, secondary, tertiary, etc.
  },
});

export default customTheme;
